    // Method to generate a random species ID based on the game
    public static int GenerateRandomSpeciesID(string gameName)
    {
        Random random = new Random();
        int[] speciesIDs;

        // Select the species IDs based on the game name
        switch (gameName)
        {
            case "LGPE":
                speciesIDs = LGPE;
                break;
            case "SWSH":
                speciesIDs = SWSH;
                break;
            case "BDSP":
                speciesIDs = BDSP;
                break;
            case "LA":
                speciesIDs = LA;
                break;
            case "SV":
                speciesIDs = SV;
                break;
            default:
                // If the game name is not recognized, return a default species ID
                return -1; // You can change this default value as needed
        }

        // Generate a random index within the range of speciesIDs array
        int randomIndex = random.Next(0, speciesIDs.Length);

        // Return the random species ID
        return speciesIDs[randomIndex];
    }
}

